import React, { useState } from "react";
import TodoListData from "../data/TodoListData";
import TodoListItem from "./TodoListItem.js";

export default function TodoList() {
  const [todoList, setTodosState] = useState(TodoListData);

  const addTodo = () => {
    setTodosState((prevTodoItems) => {
      const newTodo = {
        id: prevTodoItems[prevTodoItems.length - 1].id + 1,
        description: "New Todo",
      };
      return [...prevTodoItems, newTodo];
    });
  };

  const deleteTodo = (e) => {
    const todoDeleted = todoList.target.parentNode.id - 1
    const newTodoList = todoList.filter((todo) => todo.id !== id);
    setTodosState(newTodoList)
  };

  const todos = todoList.map((todo) => {
    return (
      <TodoListItem
        key={todo.id}
        description={todo.description}
        deleteTodo={deleteTodo}
      ></TodoListItem>
    )
  });

  return (
    <div>
      <button className="buttonStyle" onClick={addTodo}>
        Add todo
      </button>
      {todos}
    </div>
  );
}
