import React, { useState } from "react";
import "../index.css";

export default function TodoListItem(props) {
  const description = props.description;
  const [todoItemStyle, setTodoItemStyle] = useState("li");
  const changeTodoItemStyle = () => {
    setTodoItemStyle("itemChecked");
  };
  return (
    <li>
      <input type="checkbox">{description}</input>
    </li>
  );
}
