import React, { useState } from "react";
import "../index.css";

export default function TodoListItem(props) {
    const [checked, setCheckedTodoItem] = useState(false);
    const changeTodoItemStyle = (e) => {
      setCheckedTodoItem(e.target.checked)
    };
    const isChecked = (item) => item ? "checkedTodo" : "uncheckedItem";

  return (
    <li className={isChecked(checked)}>
      {props.description}
      <input type="checkbox" onChange={changeTodoItemStyle}></input>
    </li>
  );
}
