import React, { useState } from "react";

export default function Timer() {
  const [timer, setTimer] = useState(0);
  const setTimerInSec = (value) => {
    setInterval(setTimer(value + 1), 1000);
  };
  return (
    <div>
      <p>You have used {setTimerInSec(timer)} seconds on this website</p>
    </div>
  );
}
