import React, { useState } from "react";
import TodoListData from "../data/TodoListData.js";
import "../index.css";

export default function TodoListItem(props) {
  const [todos, setTodoItemStyle] = useState(TodoListData);

  function changeTodoItemStyle(id) {
    const checkedTodo = [...todos];
    checkedTodo.forEach((item) => {
      if (item.id === id) {
        item.checked = !item.checked;
      }
    });
    setTodoItemStyle(checkedTodo);
  }

  return (
    <li className={echecked ? "checked" : "unchecked"}>
      {props.description}
      <input type="checkbox" onChange={() => changeTodoItemStyle(props.id)}></input>
    </li>
  );
}
